mapper.select:
SELECT * FROM (SELECT ROW_.*,ROWNUM ROWNUM_ FROM ( select * from (select sysuser.id , sysuser.DISPLAY_NAME displayname, T_JX_PARAMETERS.MONTH_LOAN_NUM monthloannum, T_JX_PARAMETERS.MONTH_EFFECT_NUM montheffectnum, ffje1.zffje byffje, ffje2.zffje ffje, jgnsxlx.df zsx from sys_user sysuser left join T_JX_PARAMETERS on sysuser.id=T_JX_PARAMETERS.Customer_Manager_Id left join (select sum(ffje) as zffje,sysuserid from (select distinct tkmx.ywbh, tkmx.FFJE as ffje ,b.id ,tkmx.jkrq as id,sysuser.id as sysuserid from ty_repay_tkmx tkmx, ty_customer_base b, basic_customer_information basic, sys_user sysuser, ty_kdk_jh jh, ty_repay_yehz yehz, ty_repay_lsz lsz where b.id=basic.ty_customer_id and basic.user_id= sysuser.id and b.khnm=tkmx.khh and jh.ywbh=tkmx.ywbh||'HT' and yehz.jjh=tkmx.jjh and tkmx.jjh =lsz.jjh and substr(tkmx.jkrq, '0', '4') = substr(to_char( sysdate,'yyyy'), '0', '4') and substr(lsz.jzrq, '5', '2') = to_char(sysdate,'MM') ) group by sysuserid ) ffje1 on ffje1.sysuserid=sysuser.id left join (select sum(ffje) as zffje ,sysuserid from (select distinct tkmx.ywbh, tkmx.FFJE ,b.id ,tkmx.jkrq as id,sysuser.id as sysuserid from ty_repay_tkmx tkmx, ty_customer_base b, basic_customer_information basic, sys_user sysuser, ty_kdk_jh jh, ty_repay_yehz yehz, ty_repay_lsz lsz where b.id =basic.ty_customer_id and basic.user_id=sysuser.id and b.khnm=tkmx.khh and jh.ywbh=tkmx.ywbh||'HT' and yehz.jjh=tkmx.jjh and tkmx.jjh =lsz.jjh ) group by sysuserid )ffje2 on ffje2.sysuserid=sysuser.id left join ( select sum(shlx.df) as df,shlx.sysuserid,shlx.display_name from ( select tkmx.ywbh as ywbh,basic.chinese_name,sysuser.id as sysuserid,sysuser.display_name, sum(lsz.df) as df from basic_customer_information basic, ty_customer_base base, ty_repay_tkmx tkmx, SYS_USER sysuser , ty_repay_lsz lsz where base.id =basic.ty_customer_id and basic.user_id=sysuser.id and base.khnm=tkmx.khh and lsz.jjh=tkmx.jjh and lsz.zy='批量自动扣利息'
and substr(lsz.jzrq, '0', '4') = substr(to_char( sysdate,'yyyy'), '0', '4') and substr(lsz.jzrq, '5', '2') = to_char(sysdate,'MM') group by tkmx.ywbh,basic.chinese_name,sysuser.id,sysuser.display_name ,tkmx.jkrq ) shlx group by shlx.sysuserid,shlx.display_name )jgnsxlx on jgnsxlx.sysuserid=sysuser.id ) a where 1=1
and a.ID=#{customerManagerId}
) ROW_ WHERE ROWNUM <= #{limit}*#{page} + #{limit}) WHERE ROWNUM_ > #{limit}*#{page}

select * from (select sysuser.id , sysuser.DISPLAY_NAME displayname, T_JX_PARAMETERS.MONTH_LOAN_NUM monthloannum, T_JX_PARAMETERS.MONTH_EFFECT_NUM montheffectnum, ffje1.zffje byffje, ffje2.zffje ffje, jgnsxlx.df zsx from sys_user sysuser left join T_JX_PARAMETERS on sysuser.id=T_JX_PARAMETERS.Customer_Manager_Id left join (select sum(ffje) as zffje,sysuserid from (select distinct tkmx.ywbh, tkmx.FFJE as ffje ,b.id ,tkmx.jkrq as id,sysuser.id as sysuserid from ty_repay_tkmx tkmx, ty_customer_base b, basic_customer_information basic, sys_user sysuser, ty_kdk_jh jh, ty_repay_yehz yehz, ty_repay_lsz lsz where b.id=basic.ty_customer_id and basic.user_id= sysuser.id and b.khnm=tkmx.khh and jh.ywbh=tkmx.ywbh||'HT' and yehz.jjh=tkmx.jjh and tkmx.jjh =lsz.jjh and substr(tkmx.jkrq, '0', '4') = substr(to_char( sysdate,'yyyy'), '0', '4') and substr(lsz.jzrq, '5', '2') = to_char(sysdate,'MM') ) group by sysuserid ) ffje1 on ffje1.sysuserid=sysuser.id left join (select sum(ffje) as zffje ,sysuserid from (select distinct tkmx.ywbh, tkmx.FFJE ,b.id ,tkmx.jkrq as id,sysuser.id as sysuserid from ty_repay_tkmx tkmx, ty_customer_base b, basic_customer_information basic, sys_user sysuser, ty_kdk_jh jh, ty_repay_yehz yehz, ty_repay_lsz lsz where b.id =basic.ty_customer_id and basic.user_id=sysuser.id and b.khnm=tkmx.khh and jh.ywbh=tkmx.ywbh||'HT' and yehz.jjh=tkmx.jjh and tkmx.jjh =lsz.jjh ) group by sysuserid )ffje2 on ffje2.sysuserid=sysuser.id left join ( select sum(shlx.df) as df,shlx.sysuserid,shlx.display_name from ( select tkmx.ywbh as ywbh,basic.chinese_name,sysuser.id as sysuserid,sysuser.display_name, sum(lsz.df) as df from basic_customer_information basic, ty_customer_base base, ty_repay_tkmx tkmx, SYS_USER sysuser , ty_repay_lsz lsz where base.id =basic.ty_customer_id and basic.user_id=sysuser.id and base.khnm=tkmx.khh and lsz.jjh=tkmx.jjh and lsz.zy='批量自动扣利息'
and substr(lsz.jzrq, '0', '4') = substr(to_char( sysdate,'yyyy'), '0', '4') and substr(lsz.jzrq, '5', '2') = to_char(sysdate,'MM') group by tkmx.ywbh,basic.chinese_name,sysuser.id,sysuser.display_name ,tkmx.jkrq ) shlx group by shlx.sysuserid,shlx.display_name )jgnsxlx on jgnsxlx.sysuserid=sysuser.id ) a where 1=1
and a.ID=#{customerManagerId}
